import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-webinar-section',
  templateUrl: './webinar-section.component.html',
  styleUrls: ['./webinar-section.component.scss']
})
export class WebinarSectionComponent {
  @Input() type:string ='';
  @Input() webinarData: Array<any> = [];
}
<section class="container mt-4">
  <div class="d-flex justify-content-between align-items-center mb-3">
    <h4>{{type =='live'?'Live Webinars':'Upcoming Webinars'}}</h4>
    <button class="btn">View all</button>
  </div>
  <div class="overflow">
    <div class="posts d-flex justify-content-between align-items-center px-4" [ngClass]="{'live-webinar':type=='live'}">
      <ng-container *ngFor="let postData of webinarData">
        <div class="col-md-4 h-100 post">
          <app-webinar-card [postData]="postData"></app-webinar-card>
        </div>
      </ng-container>
    </div>
  </div>
</section>
