import { Component } from '@angular/core';
import { HomeService } from '../home/home.service';

@Component({
  selector: 'app-resume-watching',
  templateUrl: './resume-watching.component.html',
  styleUrls: ['./resume-watching.component.scss']
})
export class ResumeWatchingComponent {
  videoData:Array<any> = [];
  constructor(private readonly homeService:HomeService) {
    this.videoData = this.homeService.getPrevViewedVideos();
  }

}
<section class="container mt-4">
  <div class="d-flex justify-content-between align-items-center mb-md-3">
    <h4>Continue Watching</h4>
    <button class="viewbtn btn">View all<img src="assets/icons/longArrow.png" class="d-md-none ms-2"></button>
  </div>
  <div class="overflow">
    <div class="d-flex justify-content-between align-items-center videoBox px-4">
      <ng-container *ngFor="let video of videoData">
        <div class="video">
          <img [src]="video.img" class="post-img-top" alt="webinar">

          <div class="post-body">
            <h6 class="title">
              {{video.title}}
            </h6>
            <p class="mb-2">
              by <span class="videoBy ps-1">{{video.org}}</span>  • {{video.duration}} left
            </p>
          </div>
        </div>
      </ng-container>
    </div>
  </div>
</section>
